const 摘要 = ref(&#39;&#39;);
const isStreaming = ref();

const markdown = new MarkdownIt();
const 摘要Markdown = 计算(() =&gt; {
  if (!summary.value) 返回；
  返回 markdown.render(summary.value);
})

函数 startStream(streamUrl: 字符串) {
  isStreaming.value = true;
  摘要.值 = &#39;&#39;;

  const events = new EventSource(streamUrl);

  events.onmessage = (事件) =&gt; {
    摘要.值 = 摘要.值.concat(base64Decode(event.data));
  };

  events.onerror = (事件) =&gt; {
    console.error(`EventSource.onerror: ${JSON.stringify(event, null, 2)}`);
  }
  events.addEventListener(&quot;控制&quot;, (事件) =&gt; {
    const control = JSON.parse(atob(event.data));
    console.log(`传入控制命令：${JSON.stringify(control, null, 2)}`);
    开关（控制.命令）{
      案例“完成”：
        events.close();
        休息;
      默认：
        console.log(&#39;未知的 SSE 命令。&#39;);
    }
  })
};


watch(() =&gt; props.streamUrl, () =&gt; {
  if (props.streamUrl) {
    startStream(props.streamUrl);
  }
}, {立即: true});

