item.description:
对于那些感兴趣的人，这里是相应的 next.js 前端：
&quot;使用客户端&quot;;
从“react”导入 React, { useState, useEffect };
从“dompurify”导入 DOMPurify；
从“标记”导入{标记}；
从“socket.io-client”导入io；

导入“./Assistant.css”； // 假设您将 CSS 文件命名为 Assistant.css

接口ConversationEntry {
  问题？：字符串；
  答案：字符串；
}

const socket = io(&quot;http://localhost:5328/&quot;); // 调整此 URL 以匹配您的 Flask 应用程序

const助手：React.FC = () =&gt; {
  const [问题，setQuestion] = useState(&quot;&quot;);
  const [对话，setConversation] = useState([]);
  const [statusMessage, setStatusMessage] = useState(&quot;&quot;);

  useEffect(() =&gt; {
    socket.on(&quot;assistant_update&quot;, (数据: 任意) =&gt; {
      如果（数据.答案）{
        const sanitizedAnswer = DOMPurify.sanitize(
          标记（data.answer）为字符串
        ）；
        const newEntry: ConversationEntry = {
          问题：数据.问题，
          答案：已消毒答案，
        };
        setConversation((prevConversations) =&gt; [
          ...prev对话，
          新条目，
        ]);
        设置状态消息（“”）； // 收到答复后清除状态消息
      } else if (data.message) {
        setStatusMessage(data.message); // 使用助手的最新更新更新状态消息
      }
    });

    socket.on(&quot;assistant_error&quot;, (错误: 任意) =&gt; {
      console.error(&quot;助手出错：&quot;, error.error);
      setStatusMessage(`错误：${error.error}`); // 将错误显示为状态消息
    });

    返回() =&gt; {
      socket.off(&quot;assistant_update&quot;);
      socket.off(&quot;assistant_error&quot;);
    };
  }, []);

  const handleSubmit = async (事件: React.FormEvent) =&gt; {
    event.preventDefault();
    if (!question.trim()) 返回；
    setStatusMessage(&quot;正在处理您的问题...&quot;); // 提交后立即设置处理消息
    socket.emit(&quot;start_assistant&quot;, { 问题 });
    设置问题（“”）； // 提交后重置问题输入
  };

  返回 （
    &lt;div className=&quot;assistant-container&quot;&gt;
      询问助理
      ;
        &lt;输入
          类型=“文本”
          值={问题}
          onChange={(e) =&gt;; setQuestion(e.target.value)}
          占位符=“问一个问题...”
          类名=“问题输入”
        &gt;&gt;
        &lt;按钮类型=“提交”className=“提交按钮”&gt;
          提交
        &lt;/按钮&gt;
      &lt;/表格&gt;
      {/* 在文本输入正下方显示状态消息 */}
      {statusMessage &amp;&amp; {statusMessage}}

        {conversation.map((条目, 索引) =&gt; (
          &lt;div key={index} className=&quot;conversation-entry&quot;&gt;
            {条目.问题&amp;&amp; （
              &lt;p&gt;
                &lt;strong&gt;问题：&lt;/strong&gt; {条目.问题}
              &lt;/p&gt;
            )}

              &lt;strong&gt;答案：&lt;/strong&gt;{&quot; &quot;}


        ))}


  ）；
};

导出默认助手；
