// SSE GET 路由，用于向客户端流式传输响应
router.get(“/stream-responses”, (req, res) =&gt; {
const threadId = req.query.threadId;
console.log(“从前端接收到threadId:”, threadId);
res.writeHead(200, {
“内容类型”：“文本/事件流”，
“缓存控制”：“无缓存”，
连接：“保持活动”，
});
if (threadResponses[threadId]) {
// 立即通知客户端新消息的开始
res.write(事件: messageStart\ndata: {}\n\n);
threadResponses[threadId].events.forEach((event) =&gt; {
  res.write(`事件：${event.event}\n数据：${JSON.stringify(event.data)}\n\n`);
});

threadResponses[threadId].clients.push(res);

} 其他 {
res.write(“事件：消息\n数据：找不到此线程的活动流。\n\n”);
}
req.on(“关闭”, () =&gt; {
console.log(客户端与线程 ${threadId} 断开连接);
threadResponses[threadId].clients = threadResponses[threadId].clients.filter((client) =&gt; client !== res);
});
});

