item.description:
此代码创建线程、运行并响应工具输出。我只放了相关的函数。

func (c *Assistant)retrieveAssistantFunctionCall(ctx context.Context, run openai.Run) ([]*ChatMessage, error) {
步骤，err := c.client.ListRunSteps(ctx, run.ThreadID, run.ID, openai.Pagination{})
如果错误！= nil {
返回零，错误
}
如果步骤.HasMore {
Printer.Warn(&quot;运行还有更多步骤，我们还没有处理这种情况&quot;)
}
厘米 := make([]*ChatMessage, 0)
toolCallIDs := make([]字符串, 0)
for _, s := 范围步骤.RunSteps {
if s.Type != openai.RunStepTypeToolCalls {
继续
}
for _, tc := 范围 s.StepDetails.ToolCalls {
Printer.Debug(&quot;工具调用：%v&quot;, tc.ID)
if tc.Type != openai.ToolTypeFunction {
Printer.Warn(&quot;工具调用类型不是函数&quot;)
继续
}
厘米 = 追加（厘米，&amp;ChatMessage{
元数据：make(map[string]any),
内容：tc.Function.Arguments，
})
toolCallIDs = 附加（toolCallIDs，tc.ID）
}
}

toolOutputs := make([]openai.ToolOutput, len(toolCallIDs))
对于 i，tcID := 范围 toolCallIDs {
toolOutputs[i] = openai.ToolOutput{
工具调用ID：tcID，
输出：`{“成功”：true}`，
}
}

// 响应函数调用来处理下一条消息
if _, err = c.client.SubmitToolOutputs(ctx, run.ThreadID, run.ID, openai.SubmitToolOutputsRequest{
工具输出：工具输出，
});错误！=零{
返回零，错误
}
返回厘米，零
}

