}
}
运行，错误：= c.client.CreateThreadAndRun(ctx, openai.CreateThreadAndRunRequest{
运行请求：openai.RunRequest{
AssistantID：c.assistantID，
},
主题：openai.ThreadRequest{
消息：tm，
},
})
如果错误！= nil {
返回零，错误
}
log.Printf(&quot;运行 id %v 和线程 id %v&quot;, run.ID, run.ThreadID)
status, err := c.waitForStatusToBeAcceptable(ctx, &amp;run, openai.RunStatusCompleted, openai.RunStatusRequiresAction)
如果错误！= nil {
返回零，错误
}
l := len(消息)
已处理消息 := 0
返回的消息 := make([]*ChatMessage, 0)
for status == openai.RunStatusRequiresAction &amp;&amp;已处理消息 &lt;升{
Printer.Debug(&quot;已处理的消息 %d/%d&quot;,processedMessage,l)
