            form.append(&#39;file&#39;, fileBuffer, {filename: &#39;audio.webm&#39;, contentType: &#39;audio/webm&#39;})
            form.append(&#39;model&#39;, &#39;whisper-1&#39;);

            axios.post(&#39;https://api.openai.com/v1/audio/transcriptions&#39;, 表单,
                {
                    标题：{...form.getHeaders(), ...headers}
