 private async void button13_Click(object sender, EventArgs e)
        {
            textBox6.Clear();
            var api = new OpenAI_API.OpenAIAPI(&quot;API-TOKEN&quot;);

            var chat = api.Chat.CreateConversation();

            chat.AppendUserInput(&quot;根据以下文本创建一个 500-950 个字符的产品描述。技术数据应以涂层形式给出，标题为“技术特性”。整个描述应使用易于阅读的英语书写：“+文本框2.文本);

            等待 chat.StreamResponseFromChatbotAsync(res =&gt;
            {
                textBox6.AppendText(res);
            });
        }
    }

